// Extract and combine metrics from both DataForSEO responses
const keywordsData = $input.item(0).json;
const backlinksData = $input.item(1).json;
const marketInfo = $('Loop Over Markets').item.json;

// Parse keywords response
let keywordsMetrics = {
  total_keywords: 0,
  keywords_top_1: 0,
  keywords_top_3: 0,
  keywords_top_10: 0,
  keywords_top_20: 0,
  keywords_top_30: 0,
  average_ranking: 0,
  estimated_traffic: 0
};

if (keywordsData?.tasks?.[0]?.result?.[0]?.items) {
  const items = keywordsData.tasks[0].result[0].items;
  let totalRank = 0;

  items.forEach(item => {
    const rank = item.ranked_serp_element?.serp_item?.rank_absolute;
    if (rank) {
      keywordsMetrics.total_keywords++;
      totalRank += rank;

      if (rank === 1) keywordsMetrics.keywords_top_1++;
      if (rank <= 3) keywordsMetrics.keywords_top_3++;
      if (rank <= 10) keywordsMetrics.keywords_top_10++;
      if (rank <= 20) keywordsMetrics.keywords_top_20++;
      if (rank <= 30) keywordsMetrics.keywords_top_30++;

      // Estimate traffic (simplified CTR model)
      const searchVolume = item.keyword_data?.keyword_info?.search_volume || 0;
      const ctr = rank <= 3 ? 0.15 : (rank <= 10 ? 0.05 : 0.01);
      keywordsMetrics.estimated_traffic += searchVolume * ctr;
    }
  });

  if (keywordsMetrics.total_keywords > 0) {
    keywordsMetrics.average_ranking = totalRank / keywordsMetrics.total_keywords;
  }
}

// Parse backlinks response
let backlinksMetrics = {
  domain_rating: 0,
  total_backlinks: 0,
  referring_domains: 0,
  dofollow_backlinks: 0
};

if (backlinksData?.tasks?.[0]?.result?.[0]) {
  const result = backlinksData.tasks[0].result[0];
  backlinksMetrics.domain_rating = result.rank || 0;
  backlinksMetrics.total_backlinks = result.backlinks || 0;
  backlinksMetrics.referring_domains = result.referring_domains || 0;
  backlinksMetrics.dofollow_backlinks = result.referring_links_dofollow || 0;
}

// Calculate costs
const keywordsCost = keywordsData?.tasks?.[0]?.cost || 0;
const backlinksCost = backlinksData?.tasks?.[0]?.cost || 0;

// Return data formatted for Supabase schema
return {
  json: {
    // Market reference (will be converted to UUID in next node)
    market_id: marketInfo.id,
    market_code: marketInfo.code,
    market_name: marketInfo.name,
    domain: marketInfo.domain,
    date: new Date().toISOString().split('T')[0],

    // Authority metrics (matches daily_metrics schema)
    domain_rating: backlinksMetrics.domain_rating,
    total_backlinks: backlinksMetrics.total_backlinks,
    referring_domains: backlinksMetrics.referring_domains,
    dofollow_backlinks: backlinksMetrics.dofollow_backlinks,

    // Ranking metrics (matches daily_metrics schema)
    average_ranking: Number(keywordsMetrics.average_ranking.toFixed(2)),
    total_ranked_keywords: keywordsMetrics.total_keywords,
    keywords_top_1: keywordsMetrics.keywords_top_1,
    keywords_top_3: keywordsMetrics.keywords_top_3,
    keywords_top_10: keywordsMetrics.keywords_top_10,
    keywords_top_20: keywordsMetrics.keywords_top_20,
    keywords_top_30: keywordsMetrics.keywords_top_30,

    // Traffic metrics (matches daily_metrics schema)
    estimated_traffic_value: Number(keywordsMetrics.estimated_traffic.toFixed(2)),
    organic_keywords_count: keywordsMetrics.total_keywords,

    // Cost tracking (for api_costs table)
    keywords_api_cost: keywordsCost,
    backlinks_api_cost: backlinksCost,
    total_api_cost: keywordsCost + backlinksCost
  }
};

// Check for significant changes (anomalies)
const current = $input.first().json;

if (!current || !current.domain_rating) {
  return { json: { has_anomalies: false } };
}

const drDelta = current.domain_rating_delta || 0;
const rankingDelta = current.average_ranking_delta || 0;

// Define thresholds
const thresholds = {
  domain_rating: {
    high: 5,    // DR change >= 5 points
    medium: 3   // DR change >= 3 points
  },
  ranking: {
    high: 3,    // Ranking change >= 3 positions
    medium: 1.5 // Ranking change >= 1.5 positions
  }
};

const anomalies = [];

// Check Domain Rating anomalies
if (Math.abs(drDelta) >= thresholds.domain_rating.high) {
  anomalies.push({
    metric: 'Domain Rating',
    change: drDelta,
    severity: 'HIGH',
    direction: drDelta > 0 ? 'â†‘' : 'â†“',
    message: `Domain Rating ${drDelta > 0 ? 'increased' : 'decreased'} by ${Math.abs(drDelta)} points`
  });
} else if (Math.abs(drDelta) >= thresholds.domain_rating.medium) {
  anomalies.push({
    metric: 'Domain Rating',
    change: drDelta,
    severity: 'MEDIUM',
    direction: drDelta > 0 ? 'â†‘' : 'â†“',
    message: `Domain Rating ${drDelta > 0 ? 'increased' : 'decreased'} by ${Math.abs(drDelta)} points`
  });
}

// Check Average Ranking anomalies (note: higher rank = worse position)
if (Math.abs(rankingDelta) >= thresholds.ranking.high) {
  anomalies.push({
    metric: 'Average Ranking',
    change: rankingDelta,
    severity: 'HIGH',
    direction: rankingDelta > 0 ? 'â†“' : 'â†‘',
    message: `Average ranking ${rankingDelta > 0 ? 'dropped' : 'improved'} by ${Math.abs(rankingDelta).toFixed(1)} positions`
  });
} else if (Math.abs(rankingDelta) >= thresholds.ranking.medium) {
  anomalies.push({
    metric: 'Average Ranking',
    change: rankingDelta,
    severity: 'MEDIUM',
    direction: rankingDelta > 0 ? 'â†“' : 'â†‘',
    message: `Average ranking ${rankingDelta > 0 ? 'dropped' : 'improved'} by ${Math.abs(rankingDelta).toFixed(1)} positions`
  });
}

return {
  json: {
    has_anomalies: anomalies.length > 0,
    anomaly_count: anomalies.length,
    anomalies: anomalies,
    market_code: current.market_code,
    market_name: current.market_name,
    domain: current.domain,
    date: current.date,
    current_dr: current.domain_rating,
    current_ranking: current.average_ranking,
    keywords_top_10: current.keywords_top_10
  }
};

