// GLOBAL WORKFLOW SETTINGS
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Configure all parameters here, then reference throughout workflow

const settings = {
  // Core Settings
  TARGET_DOMAIN: "indiecampers.com",

  // Markets Configuration (DataForSEO location codes)
  MARKETS: [
    {"country":"PT","language":"pt-PT","location":"Lisbon","location_code":2620},
    {"country":"ES","language":"es-ES","location":"Madrid","location_code":2724},
    {"country":"FR","language":"fr-FR","location":"Paris","location_code":2250},
    {"country":"DE","language":"de-DE","location":"Berlin","location_code":2276},
    {"country":"GB","language":"en-GB","location":"London","location_code":2826}
  ],

  // Device Types
  DEVICE_TYPES: ["desktop", "mobile"],

  // Competitor Discovery
  MAX_COMPETITORS_PER_MARKET: 20,
  TOP_KEYWORDS_PER_DOMAIN: 1000,

  // Filtering Thresholds
  RANK_THRESHOLD: 20,         // Only consider keywords where competitor ranks â‰¤ 20
  KD_MAX: 45,                 // Max keyword difficulty
  MIN_VOL: 80,                // Min monthly search volume
  CLICK_POTENTIAL_MIN: 0.35,  // Min CTR potential

  // SERP Features to Prioritize
  SERP_FEATURES_INCLUDE: [
    "featured_snippet",
    "faqs",
    "maps",
    "sitelinks",
    "video",
    "people_also_ask"
  ],

  // Monitoring
  REFRESH_WINDOW_DAYS: 14,
  ALERT_SURGE_DELTA: 10,  // Position change threshold for alerts

  // Output Configuration
  SHEET_ID: "YOUR_GOOGLE_SHEET_ID_HERE",  // Replace with your Sheet ID
  TAG_PROJECT: "IC-DFSEO-INTEL",

  // Opportunity Score Weights (must sum to ~1.0)
  WEIGHTS: {
    w1_volume: 0.35,              // Search volume importance
    w2_click_potential: 0.25,      // CTR potential importance
    w3_serp_features: 0.15,        // SERP feature boost
    w4_keyword_difficulty: 0.15,   // Inverse KD weight (easier = better)
    w5_commercial_intent: 0.10,    // Transactional value
    w6_competitor_strength: 0.20   // Authority penalty
  },

  // Rate Limiting
  RATE_LIMIT: {
    calls_per_hour: 2000,
    backoff_initial_ms: 1000,
    backoff_multiplier: 2,
    max_retries: 3
  }
};

// Validate settings
if (!settings.SHEET_ID || settings.SHEET_ID === "YOUR_GOOGLE_SHEET_ID_HERE") {
  throw new Error("âš ï¸ SHEET_ID not configured! Update Global Settings node.");
}

const weightSum = Object.values(settings.WEIGHTS).reduce((a,b) => a+b, 0);
if (Math.abs(weightSum - 1.0) > 0.01) {
  console.warn(`âš ï¸ Weights sum to ${weightSum.toFixed(2)}, should be ~1.0`);
}

return [{ json: { settings } }];

// Create all market Ã— device combinations
const settings = $node["Global Settings"].json.settings;
const combinations = [];

settings.MARKETS.forEach(market => {
  settings.DEVICE_TYPES.forEach(device => {
    combinations.push({
      ...market,
      device: device,
      run_id: `${market.country}_${device}_${Date.now()}`,
      settings: settings  // Pass settings to all downstream nodes
    });
  });
});

console.log(`âœ… Prepared ${combinations.length} marketÃ—device combinations`);
return combinations.map(c => ({ json: c }));
